import React, { useState } from 'react';

const Filter = ({ onDataFiltered }) => {
  const [selectedYear, setSelectedYear] = useState('');
  const [data,setData] =  useState([]);
  const handleFilter = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/data/year/${selectedYear}`);
      if (!response.ok) {
        throw new Error('Failed to fetch data');
      }
      const data = await response.json();
      setData(data);
      console.log(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };



  // Generate years from 2000 to 2040
  const startYear = 2000;
  const endYear = 2040;
  const years = Array.from({ length: endYear - startYear + 1 }, (_, index) => startYear + index);

 

  return (
    <div>
      <label htmlFor="year">Select Year:</label>
      <select id="year" value={selectedYear} onChange={(e) => setSelectedYear(e.target.value)}>
        <option value="">All Years</option>
        {years.map(year => (
          <option key={year} value={year}>{year}</option>
        ))}
      </select>
      <button onClick={handleFilter}>Apply Filter</button>
    </div>
  );
};

export default Filter;
