import React, { useEffect, useState } from 'react';


const App = () => {


  useEffect(() => {
    axios.get('http://localhost:5000/api/data')
      .then(response => {
        setData(response.data);
        setFilteredData(response.data);
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({ ...filters, [name]: value });
  };

  const applyFilters = () => {
    let filtered = data;
    Object.keys(filters).forEach(key => {
      if (filters[key]) {
        filtered = filtered.filter(item => item[key] === filters[key]);
      }
    });
    setFilteredData(filtered);
  };

  useEffect(() => {
    applyFilters();
  }, [filters]);

  useEffect(() => {
    drawChart();
  }, [filteredData]);

  const drawChart = () => {
    d3.select('#chart').selectAll('*').remove();

    const svg = d3.select('#chart')
      .append('svg')
      .attr('width', 800)
      .attr('height', 400);

    const margin = { top: 20, right: 30, bottom: 40, left: 40 };
    const width = +svg.attr('width') - margin.left - margin.right;
    const height = +svg.attr('height') - margin.top - margin.bottom;

    const x = d3.scaleBand().range([0, width]).padding(0.1);
    const y = d3.scaleLinear().range([height, 0]);

    const g = svg.append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`);

    x.domain(filteredData.map(d => d.title));
    y.domain([0, d3.max(filteredData, d => d.intensity)]);

    g.append('g')
      .attr('class', 'axis axis--x')
      .attr('transform', `translate(0,${height})`)
      .call(d3.axisBottom(x));

    g.append('g')
      .attr('class', 'axis axis--y')
      .call(d3.axisLeft(y).ticks(10));

    g.selectAll('.bar')
      .data(filteredData)
      .enter().append('rect')
      .attr('class', 'bar')
      .attr('x', d => x(d.title))
      .attr('y', d => y(d.intensity))
      .attr('width', x.bandwidth())
      .attr('height', d => height - y(d.intensity));
  };

  return (
    <div>
      <h1>Data Visualization Dashboard</h1>
      <div>
        <label>End Year: <input name="endYear" type="number" value={filters.endYear} onChange={handleFilterChange} /></label>
        <label>Topic: <input name="topic" type="text" value={filters.topic} onChange={handleFilterChange} /></label>
        <label>Sector: <input name="sector" type="text" value={filters.sector} onChange={handleFilterChange} /></label>
        <label>Region: <input name="region" type="text" value={filters.region} onChange={handleFilterChange} /></label>
        <label>Pestle: <input name="pestle" type="text" value={filters.pestle} onChange={handleFilterChange} /></label>
        <label>Source: <input name="source" type="text" value={filters.source} onChange={handleFilterChange} /></label>
        <label>Country: <input name="country" type="text" value={filters.country} onChange={handleFilterChange} /></label>
      </div>
      <div id="chart"></div>
    </div>
  );
};

export default App;
