import React, { useRef, useEffect } from 'react';
import * as d3 from 'd3';

const Pgraph = ({ serverData }) => {
  const ref = useRef();

  useEffect(() => {
    d3.select(ref.current).selectAll('*').remove();

    if (Array.isArray(serverData.data) && serverData.data.length > 0) {
      let uniquePestle = [];
      serverData.data.forEach((i) => {
        if (!uniquePestle.includes(i.pestle) && i.pestle !== '') {
          uniquePestle.push(i.pestle);
        }
      });

      const pestleCount = uniquePestle.map((item) => ({
        pestle: item,
        count: serverData.data.filter((i) => i.pestle === item).length,
      }));

      const width = 400;
      const height = 400;
      const radius = Math.min(width, height) / 2;
      const color = d3.scaleOrdinal(d3.schemeCategory10);
      const arc = d3
        .arc()
        .outerRadius(radius - 10)
        .innerRadius(0);

      const pie = d3
        .pie()
        .sort(null)
        .value((d) => d.count);

      const svg = d3
        .select(ref.current)
        .attr('width', width + 200) // Increase width to accommodate legend
        .attr('height', height)
        .append('g')
        .attr('transform', `translate(${width / 2}, ${height / 2})`);

      const g = svg
        .selectAll('.arc')
        .data(pie(pestleCount))
        .enter()
        .append('g')
        .attr('class', 'arc');

      g.append('path')
        .attr('d', arc)
        .style('fill', (d, i) => color(i));

      // Add legend on the right side
      const legend = svg
        .selectAll('.legend')
        .data(pestleCount)
        .enter()
        .append('g')
        .attr('class', 'legend')
        .attr('transform', (d, i) => `translate(${width / 2 + radius + 20}, ${i * 20})`); // Position legend on the right

      legend
        .append('rect')
        .attr('width', 10)
        .attr('height', 10)
        .style('fill', (d, i) => color(i));

      legend
        .append('text')
        .attr('x', 15)
        .attr('y', 10)
        .text((d) => d.pestle); // Display pestle name
    }
  }, [serverData.data]);

  return <svg ref={ref}></svg>;
};

export default Pgraph;
