import React, { useRef, useEffect } from 'react';
import * as d3 from 'd3';

const Polar = ({ serverData }) => {
    const ref = useRef();

    useEffect(() => {
        let uniqueSectors = [];
        let uniqueTopics = [];
        let uniqueRegion = [];
        let uniqueCountry = [];
        let uniqueSource = [];
        let uniquePestle = [];

        serverData.data.forEach((i) => {
            if (!uniqueSectors.includes(i.sector) && i.sector !== "") {
                uniqueSectors.push(i.sector);
            }
            if (!uniqueTopics.includes(i.topic) && i.topic !== "") {
                uniqueTopics.push(i.topic);
            }
            if (!uniqueRegion.includes(i.region) && i.region !== "") {
                uniqueRegion.push(i.region);
            }
            if (!uniqueCountry.includes(i.country) && i.country !== "") {
                uniqueCountry.push(i.country);
            }
            if (!uniqueSource.includes(i.source) && i.source !== "") {
                uniqueSource.push(i.source);
            }
            if (!uniquePestle.includes(i.pestle) && i.pestle !== "") {
                uniquePestle.push(i.pestle);
            }
        });

        const data = [uniqueCountry.length, uniqueRegion.length, uniqueSource.length, uniqueTopics.length, uniqueSectors.length, uniquePestle.length];
        const labels = ["Country", "Region", "Source", "Topic", "Sector", "Pestle"];
        
        const width = 400;
        const height = 400;
        const radius = Math.min(width, height) / 2;
        const color = d3.scaleOrdinal(d3.schemeCategory10);

        const arc = d3.arc()
            .innerRadius(radius - 70)
            .outerRadius(radius);

        const pie = d3.pie()
            .sort(null)
            .value(d => d);

        const svg = d3.select(ref.current)
            .attr('width', width)
            .attr('height', height)
            .append('g')
            .attr('transform', `translate(${width / 2}, ${height / 2})`);

        const arcs = svg.selectAll('.arc')
            .data(pie(data))
            .enter().append('g')
            .attr('class', 'arc');

        arcs.append('path')
            .attr('d', arc)
            .style('fill', (d, i) => color(i));

        arcs.append('text')
            .attr('transform', d => `translate(${arc.centroid(d)})`)
            .attr('dy', '0.35em')
            .text((d, i) => labels[i]);
        
            

    }, [serverData]);

    return <svg ref={ref}></svg>;
};

export default Polar;
