import React, { useRef, useEffect } from 'react';
import * as d3 from 'd3';

const Line = ({ serverData }) => {
    const ref = useRef();

    useEffect(() => {
        let uniquePestle = [];
        serverData.forEach((i) => {
            if (!uniquePestle.includes(i.pestle) && i.pestle !== "") {
                uniquePestle.push(i.pestle);
            }
        });

        const pestleCount = uniquePestle.map((item) => ({
            pestle: item,
            count: serverData.filter((i) => i.pestle === item).length,
        }));

        const margin = { top: 20, right: 30, bottom: 40, left: 50 };
        const width = 500 - margin.left - margin.right;
        const height = 400 - margin.top - margin.bottom;

        const svg = d3.select(ref.current)
            .attr('width', width + margin.left + margin.right)
            .attr('height', height + margin.top + margin.bottom)
            .append('g')
            .attr('transform', `translate(${margin.left},${margin.top})`);

        const x = d3.scaleBand()
            .domain(uniquePestle)
            .range([0, width])
            .padding(0.1);

        const y = d3.scaleLinear()
            .domain([0, d3.max(pestleCount, d => d.count)])
            .nice()
            .range([height, 0]);

        const line = d3.line()
            .x(d => x(d.pestle))
            .y(d => y(d.count))
            .curve(d3.curveMonotoneX);

        svg.append('path')
            .datum(pestleCount)
            .attr('fill', 'none')
            .attr('stroke', 'steelblue')
            .attr('stroke-width', 1.5)
            .attr('d', line);

        svg.append('g')
            .attr('class', 'x-axis')
            .attr('transform', `translate(0,${height})`)
            .call(d3.axisBottom(x));

        svg.append('g')
            .attr('class', 'y-axis')
            .call(d3.axisLeft(y));
    }, [serverData]);

    return <svg ref={ref}></svg>;
};

export default Line;
