import React, { useState, useEffect } from 'react';
import Accordion from './Accordian/Accordian';

const App = () => {
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [selectedYear, setSelectedYear] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5000/api/data/all')
      .then(response => response.json())
      .then(data => {
        setData(data);
        setFilteredData(data);
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  const filterByYear = (year) => {
    const dataArray = Object.values(data); // Convert data object to array
    if (year === null) {
      setFilteredData(dataArray);
    } else {
      const filtered = dataArray.filter(item => item.year === year);
      setFilteredData(filtered);
    }
    setSelectedYear(year);
  };

  const resetFilter = () => {
    setFilteredData(data);
    setSelectedYear(null);
  };

  const startYear = 2000;
  const endYear = 2040;
  const years = Array.from({ length: endYear - startYear + 1 }, (_, index) => startYear + index);

  return (
    <div>
      <nav>
        <ul>
          <li>
            <select value={selectedYear || ''} onChange={(e) => filterByYear(e.target.value)}>
              <option value="">All Years</option>
              {years.map(year => (
                <option key={year} value={year}>{year}</option>
              ))}
            </select>
          </li>
          <li>
            <button onClick={resetFilter}>Reset Filter</button>
          </li>
        </ul>
      </nav>
      <Accordion data={filteredData} />
    </div>
  );
};

export default App;
