import React from 'react'
import Aaccordion from 'react-bootstrap/Accordion';
import Bar from '../Graphs/Bar';
import Polar from '../Graphs/Polar';
import Line from '../Graphs/Line';
import Doughnut from '../Graphs/Doghnut';
import Pgraph from "../Graphs/Pgraph";




const Accordion = ({ data }) => {
    const [openIndex, setOpenIndex] = useState(null);

    const toggleAccordion = (index) => {
        setOpenIndex(openIndex === index ? null : index);
    };

    return (
        <div>
            <Accordion>
                <Accordion.Item eventKey="0">
                    <Accordion.Header>Polar Area and Doughnut Charts - represents number of countries, sectors, topics, pestles, sources, etc are involved</Accordion.Header>
                    <Accordion.Body>
                        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                            <Polar serverData={data} />
                            <Doughnut serverData={data} />
                        </div>
                    </Accordion.Body>
                </Accordion.Item>
                <Accordion.Item eventKey="1">
                    <Accordion.Header>Bar Chart- represents number of projects in each sector</Accordion.Header>
                    <Accordion.Body>
                        <Bar serverData={data} />
                    </Accordion.Body>
                </Accordion.Item>
                <Accordion.Item eventKey="2">
                    <Accordion.Header>Line Chart- represents number of projects as per pestle</Accordion.Header>
                    <Accordion.Body>
                        <Line serverData={data} />
                    </Accordion.Body>
                </Accordion.Item>
                <Accordion.Item eventKey="3">
                    <Accordion.Header>Radar and Pie Charts- represents number of projects as per pestle</Accordion.Header>
                    <Accordion.Body>
                        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                            
                            <Pgraph serverData={data} />
                        </div>
                    </Accordion.Body>
                </Accordion.Item>

            </Accordion>
        </div>
    );
};

export default Accordion;
