import React, { useState } from 'react';

const Filter = ({ data, onDataFiltered }) => {
  const [selectedYear, setSelectedYear] = useState(null);

  const filterByYear = (year) => {
    setSelectedYear(year);
    // Perform filtering based on the selected year
    const filteredData = year ? data.filter(item => item.year === year) : data;
    // Pass the filtered data back to the parent component
    onDataFiltered(filteredData);
  };

  // Extract unique years from the data
  const uniqueYears = Array.from(new Set(data.map(item => item.year)));

  return (
    <div>
      <label htmlFor="year">Select Year:</label>
      <select id="year" value={selectedYear || ''} onChange={(e) => filterByYear(e.target.value)}>
        <option value="">All Years</option>
        {uniqueYears.map(year => (
          <option key={year} value={year}>{year}</option>
        ))}
      </select>
    </div>
  );
};

export default Filter;
